package javademo;
import java.util.Scanner;

public class MulFloatNumbers {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		Scanner s = new Scanner(System.in);
		float a = s.nextFloat();
		float b = s.nextFloat();
		
		double result = a*b;
		
		System.out.println("The product is: " + result);

	}

}
